	<script>
		ymaps.ready(init);
		function init() {
			const coordinates = [<?php echo $coordinate; ?>];
			const addressContent = '<?php the_field('address', 'option'); ?>';
			const myMap = new ymaps.Map('map', {
				center: coordinates,
				zoom: 16,
				controls: []
			});
			myMap.behaviors.disable('scrollZoom');
			const addressPlacemark = new ymaps.Placemark(coordinates, {
				balloonContentHeader: addressContent,
			}, {
				iconLayout: 'default#image',
				iconImageHref: '/wp-content/uploads/2024/10/iv-point.svg',
				iconImageSize: [96, 96],
				iconImageOffset: [-48, -96],
			});
			myMap.geoObjects.add(addressPlacemark);
		}
	</script>
